import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import CustomForm from "../components/Form";
import Tweet from "../components/Tweet";
import { withRouter } from "react-router-dom";
import Pagination from "../components/Pagination";
import * as actions from "../actions/tweetActions";

class TweetList extends React.Component {
  state = {
    tweets: [],
    page: 1,
    count: 0,
    range: 10
  };

  componentDidMount() {
    this.props.getTweets({ page: 1 });
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.token && nextProps.tweets.results) {
      this.setState({
        tweets: nextProps.tweets.results,
        count: nextProps.tweets.count
      });
    }
  }

  onChange = page => {
    this.props.getTweets({ page: page });
    this.setState({ page: page });
  };

  render() {
    return (
      <div>
        {this.props.token ? (
          <div>
            <h2>Tweets</h2>
            <Tweet data={this.state.tweets} />
            <Pagination
              data={{
                page: this.state.page,
                total: this.state.count,
                range: this.state.range
              }}
              nextPage={this.onChange}
            />
            <h2>Create Tweet</h2>
            <CustomForm requestType="post" tweetID={null} btnText="create" />
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.authReducer.token,
    tweets: state.tweetReducer.tweets,
    authLoading: state.authReducer.loading
  };
};
const mapDispatchToProps = dispatch => {
  return {
    getTweets: page => dispatch(actions.getTweets(page))
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TweetList)
);
