import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import CustomForm from "../components/Form";
import Tweet from "../components/Tweet";
import { withRouter } from "react-router-dom";
import Pagination from "../components/Pagination";
import * as actions from "../actions/tweetActions";

class TweetList extends React.Component {
  state = {
    tweets: [],
    page: 1,
    count: 0,
    range: 10
  };

  componentDidMount() {
    if (this.props.token !== undefined && this.props.token !== null) {
      const page = 1;
      this.props.getTweets(page, this.props.token);
    }
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.token) {
      this.setState({
        tweets: nextProps.tweets.results,
        count: nextProps.tweets.count
      });
    }
  }

  onChange = page => {
    this.props.getTweets(page, this.props.token);
    this.setState({ page: page });
  };

  render() {
    return (
      <div>
        {this.props.loading ? (
          <h2>Loading....</h2>
        ) : (
          <div>
            <h2>Tweets</h2>
            <Tweet data={this.state.tweets} />
            <Pagination
              data={{
                page: this.state.page,
                total: this.state.count,
                range: this.state.range
              }}
              nextPage={this.onChange}
            />
            <h2>Create Tweet</h2>
            <CustomForm requestType="post" tweetID={null} btnText="create" />
          </div>
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.authReducer.token,
    tweets: state.tweetReducer.tweets,
    loading: state.tweetReducer.loading
  };
};
const mapDispatchToProps = dispatch => {
  return {
    getTweets: (page, token) => dispatch(actions.getTweets(page, token))
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TweetList)
);
