import React from "react";
import { Form, Input, Button } from "antd";
import { connect } from "react-redux";
import axios from "axios";
import * as actions from "../actions/tweetActions";

const FormItem = Form.Item;

class CustomForm extends React.Component {
  handleFormSubmit = async (event, requestType, tweetID) => {
    event.preventDefault();

    const postObj = {
      content: event.target.elements.content.value
    };

    if (requestType === "post") {
      await axios
        .post("http://127.0.0.1:8000/api/tweets/", postObj)
        .then(res => {
          if (res.status === 201) {
            this.props.history.push(`/`);
          }
        });
    } else if (requestType === "put") {
      this.props.updateTweet(tweetID, postObj, this.props.token);
    }
  };

  render() {
    return (
      <div>
        <Form
          onSubmit={event =>
            this.handleFormSubmit(
              event,
              this.props.requestType,
              this.props.tweetID
            )
          }
        >
          <FormItem label="Content">
            <Input name="content" placeholder="Enter some content ..." />
          </FormItem>
          <FormItem>
            <Button type="primary" htmlType="submit">
              {this.props.btnText}
            </Button>
          </FormItem>
        </Form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    token: state.token
  };
};
const mapDispatchToProps = dispatch => {
  return {
    postTweet: tweet => dispatch(actions.postTweet(tweet)),
    updateTweet: (id, updateObj, token) =>
      dispatch(actions.updateTweet(id, updateObj, token))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CustomForm);
