import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import { Login } from "./components/Login";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { connected: "not connected" };
  }
  callAPI() {
    fetch("/connected")
      .then(res => res.json())
      .then(res => {
        console.log(res);
        this.setState({ connected: res });
      });
  }

  componentWillMount() {
    this.callAPI();
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <ul>{this.state.connected}</ul>
        </header>
      </div>
    );
  }
}

export default App;
