import React from "react";
import axios from "axios";
import CustomForm from "../components/Form";
import { connect } from "react-redux";
import { Card, Button, Form } from "antd";
import { withRouter } from "react-router-dom";
import * as actions from "../actions/tweetActions";
import Hoc from "../hoc/hoc";

class TweetDetail extends React.Component {
  state = {
    content: "",
    author: ""
  };
  componentWillReceiveProps(nextProps) {
    if (
      nextProps.token !== this.props.token &&
      nextProps.token !== undefined &&
      nextProps.token !== null
    ) {
      this.props.getDetailTweet(
        this.props.match.params.tweetID,
        nextProps.token
      );
    }
  }
  componentDidMount() {
    console.log("mounted", this.props.token);
    if (this.props.token !== null && this.props.token !== undefined)
      this.props.getDetailTweet(
        this.props.match.params.tweetID,
        this.props.token
      );
  }
  handleDelete = async event => {
    event.preventDefault();
    if (this.props.token !== null) {
      const tweetID = this.props.match.params.tweetID;
      axios.defaults.headers = {
        "Content-Type": "application/json",
        Authorization: `Token ${this.props.token}`
      };
      await axios
        .delete(`http://127.0.0.1:8000/api/tweets/${tweetID}/`)
        .then(res => {
          this.props.history.push("/");
        });
    }
  };

  render() {
    return (
      <Hoc>
        {this.state.author !== "" ? (
          <Card>
            <h1>{this.state.author}</h1>
            <p>{this.state.content}</p>
          </Card>
        ) : (
          <h1>loading</h1>
        )}

        {localStorage.getItem("user") === this.state.author ? (
          <div>
            <CustomForm
              {...this.props}
              requestType="put"
              tweetID={this.props.match.params.tweetID}
              btnText="Update"
            />
            <Form onSubmit={this.handleDelete} className="float-right">
              <Button type="danger" htmlType="submit">
                Delete
              </Button>
            </Form>
          </div>
        ) : (
          <div />
        )}
      </Hoc>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.authReducer.token,
    tweet: state.tweetReducer.tweets
  };
};
const mapDispatchToProps = dispatch => {
  return {
    getDetailTweet: (id, token) => dispatch(actions.getDetailTweet(id, token))
  };
};
export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TweetDetail)
);
