import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import CustomForm from "../components/Form";
import Tweet from "../components/Tweet";
import { withRouter } from "react-router-dom";
import Pagination from "../components/Pagination";

class TweetList extends React.Component {
  state = {
    tweets: [],
    page: 1,
    count: 0,
    range: 0,
    next: ""
  };
  fetchArticles = () => {
    axios
      .get(`http://127.0.0.1:8000/api/tweets/?page=${this.state.page}`)
      .then(res => {
        console.log(res);
        this.setState({
          tweets: res.data.results,
          count: res.data.count,
          range: res.data.results.length,
          next: res.data.next
        });
        console.log(this.state);
      });
  };
  componentDidMount() {
    this.fetchArticles();
  }
  componentDidUpdate(prevProps, prevState) {
    console.log(prevState);
    console.log(this.state);
    if (prevState !== this.state) {
      axios.defaults.headers = {
        "Content-Type": "application/json",
        Authorization: this.props.token
      };
      console.log(this.state.page);
      axios
        .get(`http://127.0.0.1:8000/api/tweets/?page=${this.state.page}`)
        .then(res => {
          this.setState({
            tweets: res.data.results,
            page: res.data.page
          });
        });
    }
  }

  render() {
    return (
      <div>
        {this.props.token ? (
          <div>
            <h2>Tweets</h2>
            <Tweet data={this.state.tweets} />
            <Pagination
              postsPerPage={this.state.range}
              totalPosts={this.state.count}
              paginate={this.state.page}
            />
            <h2>Create Tweet</h2>
            <CustomForm requestType="post" tweetID={null} btnText="create" />
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.token
  };
};
export default withRouter(connect(mapStateToProps)(TweetList));
