import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import CustomForm from "../components/Form";
import Tweet from "../components/Tweet";
import { withRouter } from "react-router-dom";
import Pagination from "../components/Pagination";
import * as actions from "../actions/tweetActions";

class TweetList extends React.Component {
  state = {
    tweets: [],
    page: 1,
    count: 0,
    range: 0,
    next: ""
  };
  fetchArticles = () => {
    console.log(this.state.page);
    this.props.getTweets({ page: 1 });
    console.log(this.props.tweets);
    /*axios
      .get(`http://127.0.0.1:8000/api/tweets/?page=${this.state.page}`)
      .then(res => {
        console.log(res);
        this.setState({
          tweets: res.data.results,
          count: res.data.count,
          range: res.data.results.length,
          next: res.data.next
        });
        console.log(this.state);
      });*/
  };
  componentDidMount() {
    this.fetchArticles();
  }
  componentWillReceiveProps(nextProps) {
    console.log(nextProps);
    console.log(this.state);
    console.log("updated");
    console.log(nextProps);
    //if (nextProps.token) {
    //this.setState({ tweets: this.props.tweets.results });
    //}
  }

  onChange = page => {
    console.log(page);
    //this.setState({ page: page });
    console.log(this.state);
    this.props.getTweets({ page: page });
    console.log("onchange");
    this.setState({ page: page });
    /*axios
      .get(`http://127.0.0.1:8000/api/tweets/?page=${this.state.page}`)
      .then(res => {
        this.setState({
          tweets: res.data.results,
          count: res.data.count,
          range: res.data.results.length,
          next: res.data.next
        });
        console.log(this.state);
      });*/
  };

  render() {
    return (
      <div>
        {this.props.token ? (
          <div>
            <h2>Tweets</h2>
            <Tweet data={this.props.tweets} />
            <Pagination
              data={{
                page: this.state.page,
                total: this.state.count,
                range: this.state.range
              }}
              nextPage={this.onChange}
            />
            <h2>Create Tweet</h2>
            <CustomForm requestType="post" tweetID={null} btnText="create" />
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.authReducer.token,
    tweets: state.tweetReducer.tweets
  };
};
const mapDispatchToProps = dispatch => {
  console.log("DISPATCHING TWEETS");
  return {
    getTweets: page => dispatch(actions.getTweets(page))
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TweetList)
);
