import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import CustomForm from "../components/Form";
import Tweet from "../components/Tweet";
import { withRouter } from "react-router-dom";
import { Pagination } from "antd";

class TweetList extends React.Component {
  state = {
    tweets: [],
    page: 1
  };
  fetchArticles = () => {
    axios
      .get(`http://127.0.0.1:8000/api/tweets/?page=${this.state.page}`)
      .then(res => {
        console.log(res);
        this.setState({
          tweets: res.data.results
        });
      });
  };
  componentDidMount() {
    this.fetchArticles();
  }
  componentWillReceiveProps(newProps) {
    if (newProps.token) {
      axios.defaults.headers = {
        "Content-Type": "application/json",
        Authorization: newProps.token
      };
      axios
        .get(`http://127.0.0.1:8000/api/tweets/?page=${this.state.page}`)
        .then(res => {
          this.setState({
            tweets: res.data.results
          });
        });
    } else {
      console.log("no token");
      this.setState({
        tweets: []
      });
    }
  }

  render() {
    return (
      <div>
        {this.props.token ? (
          <div>
            <h2>Tweets</h2>
            <Tweet data={this.state.tweets} />
            <Pagination
              total={85}
              showTotal={(total, range) =>
                `${range[0]}-${range[1]} of ${total} items`
              }
              pageSize={20}
              defaultCurrent={1}
            />
            <h2>Create Tweet</h2>
            <CustomForm requestType="post" tweetID={null} btnText="create" />
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.token
  };
};
export default withRouter(connect(mapStateToProps)(TweetList));
