import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import { List, Button } from "antd";
import { withRouter } from "react-router-dom";

class UserList extends React.Component {
  state = {
    users: [],
    following: []
  };
  fetchArticles = () => {
    axios.get("http://127.0.0.1:8000/users/").then(res => {
      this.setState({
        users: res.data
      });
      console.log(res.data);
    });
  };
  fetchFollowing = () => {
    axios
      .get(`http://127.0.0.1:8000/api/follows/${localStorage.getItem("user")}`)
      .then(res => {
        this.setState({
          following: res.data.follows
        });
        console.log(res.data.follows);
      });
  };
  componentDidMount() {
    this.fetchArticles();
    this.fetchFollowing();
  }
  componentWillReceiveProps(newProps) {
    if (newProps.token) {
      axios.defaults.headers = {
        "Content-Type": "application/json",
        Authorization: newProps.token
      };
      axios.get("http://127.0.0.1:8000/users/").then(res => {
        this.setState({
          users: res.data
        });
      });
    } else {
      console.log("no token");
      this.setState({
        users: []
      });
    }
  }
  followOrNot(user) {
    if (this.state.following.some(e => e.id === user)) {
      console.log("Exists", user);
      return true;
    }
    return false;
  }
  render() {
    for (var user in this.state.users) {
      console.log(user);
      if (this.state.following.some(e => e.id === user)) {
        console.log("Exists", user);
      } else {
      }
    }
    console.log(this.state.users);
    console.log(this.state.following);
    return (
      <div>
        {this.props.token ? (
          <div>
            <List
              itemLayout="horizontal"
              dataSource={this.state.users}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta title={<p> {item.username}</p>} />
                  {this.followOrNot(item.id) ? (
                    <Button>Follow</Button>
                  ) : (
                    <Button>Unfollow</Button>
                  )}
                </List.Item>
              )}
            />
            <List
              itemLayout="horizontal"
              dataSource={this.state.following}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta title={<p> {item.username}</p>} />
                  <Button>Follow</Button>
                </List.Item>
              )}
            />
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.token
  };
};
export default withRouter(connect(mapStateToProps)(UserList));
