import React from "react";
import axios from "axios";
import CustomForm from "../components/Form";
import { connect } from "react-redux";
import { Card, Button, Form } from "antd";
import { withRouter } from "react-router-dom";
import * as actions from "../actions/tweetActions";

class TweetDetail extends React.Component {
  state = {
    content: "",
    author: ""
  };
  componentWillReceiveProps(newProps) {
    if (newProps.token && newProps.tweet.content) {
      //this.props.getDetailTweet(this.props.match.params.tweetID);
      console.log("received", newProps);
      this.setState({
        content: newProps.tweet.content,
        author: newProps.tweet.author.username
      });
    }
  }
  componentDidMount() {
    this.props.getDetailTweet(this.props.match.params.tweetID);
    console.log("mounted?", this.props.tweet);
  }
  handleDelete = async event => {
    event.preventDefault();
    if (this.props.token !== null) {
      console.log(this.props.match.params);
      const tweetID = this.props.match.params.tweetID;
      axios.defaults.headers = {
        "Content-Type": "application/json",
        Authorization: `Token ${this.props.token}`
      };
      await axios
        .delete(`http://127.0.0.1:8000/api/tweets/${tweetID}/`)
        .then(res => {
          console.log(res);
          this.props.history.push("/");
        });
    }
  };

  render() {
    let currUser = "";
    if (this.state.tweet.author) {
      console.log(this.state.tweet.author);
      currUser = this.state.tweet.author.username;
    }
    return (
      <div>
        {this.state.tweet.content ? (
          <Card>
            <h1>{this.state.author.username}</h1>
            <p>{this.state.tweet.content}</p>
          </Card>
        ) : (
          <div />
        )}

        {localStorage.getItem("user") === this.state.tweet.author ? (
          <div>
            <CustomForm
              {...this.props}
              requestType="put"
              tweetID={this.props.match.params.tweetID}
              btnText="Update"
            />
            <Form onSubmit={this.handleDelete} className="float-right">
              <Button type="danger" htmlType="submit">
                Delete
              </Button>
            </Form>
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.authReducer.token,
    tweet: state.tweetReducer.tweets
  };
};
const mapDispatchToProps = dispatch => {
  return {
    getDetailTweet: id => dispatch(actions.getDetailTweet(id))
  };
};
export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TweetDetail)
);
