import axios from "axios";
import * as actionTypes from "./actionTypes";

export const getTweetSuccess = token => {
  return {
    type: actionTypes.GET_TWEETS_SUCCESS,
    token: token
  };
};

export const getTweets = page => {
  return dispatch => {
    axios.get(`http://127.0.0.1:8000/api/tweets/?page=${page}`).then(res => {
      this.setState({
        tweets: res.data.results,
        count: res.data.count,
        range: res.data.results.length,
        next: res.data.next
      });
      console.log(this.state);
    });
  };
};
