import { GET_TWEETS_START, GET_TWEETS_SUCCESS } from "../actions/actionTypes";
import { updateObject } from "./updateObject";

const initialState = {
  tweets: null,
  count: 0,
  range: 0,
  loading: false
};
console.log("in tweetReducer");

const getTweetSuccess = (state, action) => {
  console.log("successs", action);
  return updateObject(state, {
    tweets: action.tweets.results,
    loading: false
  });
};
const getTweetStart = (state, action) => {
  console.log("START FROM REDUCER ACTIONS");
  return updateObject(state, {
    loading: true
  });
};

export default function(state = initialState, action) {
  console.log("tweetReducer iniital", initialState);
  switch (action.type) {
    case GET_TWEETS_START:
      return getTweetStart(state, action);
    case GET_TWEETS_SUCCESS:
      return getTweetSuccess(state, action);
    default:
      return state;
  }
}
