import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import Tweet from "../components/Tweet";
import Follows from "../components/Follows";

class UserProfile extends React.Component {
  state = {
    user: "",
    follows: [],
    tweets: []
  };

  fetchProfile = () => {
    axios.defaults.xsrfHeaderName = "X-CSRFTOKEN";
    axios.defaults.xsrfCookieName = "csrftoken";
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${this.props.token}`
    };
    axios
      .get(`http://127.0.0.1:8000/api/profile/${localStorage.getItem("user")}`)
      .then(res => {
        this.setState({
          user: res.data.user.username,
          follows: res.data.follows.follows,
          tweets: res.data.tweets
        });
        console.log(res.data);
      })
      .catch(console.log(""));
  };
  componentDidMount() {
    console.log("mounted");
    //this.fetchProfile();
  }
  componentWillReceiveProps(newProps) {
    if (newProps.token) {
      axios.defaults.headers = {
        "Content-Type": "application/json",
        Authorization: newProps.token
      };
      const username = this.props.match.params.username;
      axios.get(`http://127.0.0.1:8000/api/profile/${username}`).then(res => {
        this.setState({
          user: res.data.user.username,
          follows: res.data.follows.follows,
          tweets: res.data.tweets
        });
        console.log(res.data);
      });
    } else {
      console.log("no token");
      this.setState({
        profiles: []
      });
    }
  }

  render() {
    return (
      <div>
        <h2>{this.state.user}</h2>
        <h1>Tweets</h1>
        <div>
          <Tweet data={this.state.tweets} />
          <Follows data={this.state.follows} />
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.token
  };
};
export default withRouter(connect(mapStateToProps)(UserProfile));
