import React from "react";
import axios from "axios";
import CustomForm from "../components/Form";
import { connect } from "react-redux";
import { Card, Button, Form } from "antd";

class TweetDetail extends React.Component {
  state = {
    tweet: {}
  };
  componentDidMount() {
    const tweetID = this.props.match.params.tweetID;

    axios.get(`http://127.0.0.1:8000/api/tweets/${tweetID}`).then(res => {
      this.setState({
        tweet: res.data
      });
      console.log(res.data);
    });
  }
  handleDelete = event => {
    event.preventDefault();
    console.log(this.props.match.params);
    const tweetID = this.props.match.params.tweetID;
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${this.props.token}`
    };
    axios.delete(`http://127.0.0.1:8000/api/tweets/${tweetID}/`);
    //this.props.history.push("/");
  };
  render() {
    return (
      <div>
        <Card>
          <p>{this.state.tweet.content}</p>
        </Card>
        <CustomForm
          {...this.props}
          requestType="put"
          tweetID={this.props.match.params.tweetID}
          btnText="Update"
        />
        <Form onSubmit={this.handleDelete}>
          <Button type="danger" htmlType="submit">
            Delete
          </Button>
        </Form>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.token
  };
};
export default connect(mapStateToProps)(TweetDetail);
